'use strict';
var log = require('loglevel');

var Button = function(el, game) {
	this.game = game;
	this.el = el;

	var last = 0;

	this.el.addEventListener('click', function(ev) {
		ev.stopPropagation();
		log.trace('move clicked');
		game.makeMove(game._player.id, last++);
	});
};

Button.prototype.destroy = function() {

};

/**
 * UI resize event, update bounds.
 * TODO
 *
 * @param {object} measured
 * @param {number} measured.width - Game parent width.
 * @param {number} measured.height - Game parent height.
 */
Button.prototype.resize = function(measured) {

};

module.exports = Button;
