'use strict';

var Handler = function(el, game) {
  this.el = el;
  this.game = game;

  this._onMove = this._handleMove.bind(this);
  this.game.on('move', this._onMove);
};

Handler.prototype.destroy = function() {
  this.game.off('move', this._onMove);
};

Handler.prototype._handleMove = function(playerId, moveId) {
  // TODO
  var el = document.createElement('div');
  el.innerHTML = 'move event received';

  window.requestAnimationFrame(function() {
    this.el.appendChild(el);
  }.bind(this));
};

module.exports = Handler;
